import React, {Component} from 'react';
import IndianHome from './components/Indian/Home';
import Cart from './components/Cart/Cart';
import Navbar from './components/Utils/Navbar';
import { BrowserRouter as Router, Switch, Route, Link} from 'react-router-dom';
import Index from './components/Index/index';
import 'bootstrap/dist/css/bootstrap.min.css'

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
        Idly: 0,
        Dosa: 0,
        Chapathi:0,
        Pongal: 0,
        Puri: 0,
        Aapam: 0
    }
    }

    addCardHandler = (title, value) => this.setState({[title]: value});
    render(){
        const {state} = this;
        return ( 
                <Router>
                    <div className = "App">
                        <Navbar />
                        <Switch>
                            <Route path='/' component={Index} />
                            <Route path='/indian-food' component={()=><IndianHome addCardHandler={this.addCardHandler} />} />
                            <Route path='/cart' component={()=><Cart data={state} />} />
                        </Switch>
                    </div>
                </Router>
        );
    } 
}

export default App;
