import React, {Component} from 'react';
import IndianHome from './components/Indian/Home';
import Cart from './components/Cart/Cart';
import { BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import './App.css';

class App extends Component {
    state = {
        Idly: 0,
        Dosa: 0,
        Chapathi:0,
        Pongal: 0,
        Puri: 0,
        Aapam: 0
    }
    addCardHandler = (title, value) => {
        // console.log(title)
        this.setState({[title]: value});
    }
    updateDisplay = () =>   this.props.history.push('/cart')
    render(){
        const {state} = this;
        return ( 
            <div>
                <button onClick={this.updateDisplay}>Cart</button>
                <Router>
                    <div className = "App">
                        <Switch>
                            <Route path='/indian-food' component={()=><IndianHome addCardHandler={this.addCardHandler} />} />
                            <Route path='/cart' component={()=><Cart data={state} />} />
                        </Switch>
                    </div>
                </Router>

            </div>
        );
    } 
}

export default App;
