import React, {useState} from 'react';


const FoodItem = ({title, image, addCardHandler}) => {
    const style = {
        card : {
            padding: '10px'
        },
        input: {
            width: '12px'
        }
    }
    const [value, setValue] = useState(0);
    const [visible, setVisible] = useState('hidden');
    const addOne = () => {
        setValue(value+1);
        setVisible('visible')
    };
    const removeOne = () => {
        if(value > 0) {
            setValue(value-1);
        }
        if(value < 2) {
            setVisible('hidden');
        }
    };

    return (
        <div style={style.card}>
            <img src={image} alt="" height="200" width="350"/>
            <p>{title}</p>
            <div>
                <button onClick={addOne}>+</button>
                <input type="text" value={value} style={style.input}/>
                <button onClick={removeOne}>-</button>
            </div>
            <button  style={{visibility: visible}} onClick={()=>addCardHandler(title, value)}>Add To Cart</button>
        </div>
    )
}

export default FoodItem;
