import React, {Component} from 'react';
import IndianHome from './components/Indian/Home';
import Cart from './components/Cart/Cart';
import { BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import {} from 'histor'
import './App.css';

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
        Idly: 0,
        Dosa: 0,
        Chapathi:0,
        Pongal: 0,
        Puri: 0,
        Aapam: 0
    }
    }

    addCardHandler = (title, value) => this.setState({[title]: value});
    updateDisplay = () =>   console.log(this.props);
    render(){
        const {state} = this;
        return ( 
            <div>
                <button onClick={this.updateDisplay}>Cart</button>
                <Router>
                    <div className = "App">
                        <Switch>
                            <Route path='/indian-food' component={()=><IndianHome addCardHandler={this.addCardHandler} />} />
                            <Route path='/cart' component={()=><Cart data={state} />} />
                        </Switch>
                    </div>
                </Router>

            </div>
        );
    } 
}

export default App;
