import React, {Component} from 'react';
import IndianHome from './components/Indian/Home';
import Cart from './components/Cart/Cart';
// import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import './App.css';

class App extends Component {
    state = {
        Idly: 0,
        Dosa: 0,
        Chapathi:0,
        Pongal: 0,
        Puri: 0,
        Aapam: 0,
        display: true
    }
    addCardHandler = (title, value) => {
        // console.log(title)
        this.setState({[title]: value});
    }
    updateDisplay = () => this.setState({display: !this.state.display})
    render(){
        const {state} = this;
        return ( 
            <div>
                <button onClick={this.updateDisplay}>Cart</button>
            {/* // <Router>
            //     <div className = "App">
            //         <Switch>
            //             <Route path='/indian-food' component={()=><IndianHome addCardHandler={this.addCardHandler} />} />
            //         </Switch>
            //     </div>
            // </Router> */}
            {
                state.display ? <IndianHome addCardHandler={this.addCardHandler} /> : <Cart data={state} />
            }

            </div>
        );
    } 
}

export default App;
